);INSERT INTO "CV_ResultType" VALUES('spectraCoverage','Spectra coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over multiple wavelengths of light. ValueDateTime may be fixed or controlled.','Coverage','http://vocabulary.odm2.org/resulttype/spectraCoverage');INSERT INTO "CV_ResultType" VALUES('sectionCoverage','Section coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over varying X (horizontal) and Z (depth) coordinates. ValueDateTime may be fixed or controlled.','Coverage','http://vocabulary.odm2.org/resulttype/sectionCoverage');INSERT INTO "CV_ResultType" VALUES('trajectoryCoverage','Trajectory coverage','A series of ResultValues for a single Variable, measured on a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over varying X,Y, Z, and D locations, where X and Y are horizontal coordinates, Z is a vertical coordinate, and D is the distance along the trajectory. ValueDateTime may be fixed (DTS Temperature) or controlled (glider).','Coverage','http://vocabulary.odm2.org/resulttype/trajectoryCoverage');INSERT INTO "CV_ResultType" VALUES('temporalObservation','Temporal observation','A single ResultValue for a single Variable, measured on or at a SamplingFeature, using a single Method, with specific Units, and having a specific ProcessingLevel.','Measurement','http://vocabulary.odm2.org/resulttype/temporalObservation');INSERT INTO "CV_ResultType" VALUES('timeSeriesCoverage','Time series coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature of fixed location, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over time.','Coverage','http://vocabulary.odm2.org/resulttype/timeSeriesCoverage');INSERT INTO "CV_ResultType" VALUES('pointCoverage','Point coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, with a fixed ValueDateTime, but measured over varying X,Y locations, where X and Y are horizontal coordinates.','Coverage','http://vocabulary.odm2.org/resulttype/pointCoverage');INSERT INTO "CV_ResultType" VALUES('measurement','Measurement','A single ResultValue for a single Variable, measured on or at a SamplingFeature, using a single Method, with specific Units, and having a specific ProcessingLevel.','Measurement','http://vocabulary.odm2.org/resulttype/measurement');INSERT INTO "CV_ResultType" VALUES('countObservation','Count observation','A single ResultValue for a single Variable, counted on or at a single SamplingFeature, using a single Method, and having a specific ProcessingLevel.','Measurement','http://vocabulary.odm2.org/resulttype/countObservation');INSERT INTO "CV_ResultType" VALUES('truthObservation','Truth observation','A single ResultValue for a single Variable, measured on or at a single SamplingFeature, using a single Method.','Measurement','http://vocabulary.odm2.org/resulttype/truthObservation');INSERT INTO "CV_ResultType" VALUES('transectCoverage','Transect coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over multiple locations along a transect having varying location dimensions (e.g.,  X and/or Y, where X and Y are horizontal coordintes). ValueDateTime may be fixed or controlled.','Coverage','http://vocabulary.odm2.org/resulttype/transectCoverage');INSERT INTO "CV_ResultType" VALUES('categoryObservation','Category observation','A single ResultValue for a single Variable, measured on or at a single SamplingFeature, using a single Method.','Measurement','http://vocabulary.odm2.org/resulttype/categoryObservation');INSERT INTO "CV_ResultType" VALUES('profileCoverage','Profile coverage','A series of ResultValues for a single Variable, measured on or at a single SamplingFeature, using a single Method, with specific Units, having a specific ProcessingLevel, but measured over multiple locations along a depth profile with only one varying location dimension (e.g., Z, where Z is depth). ValueDateTime may be fixed or controlled.','Coverage','http://vocabulary.odm2.org/resulttype/profileCoverage');CREATE TABLE CV_SampledMedium (
