);INSERT INTO "CV_SampledMedium" VALUES('organism','Organism','Data collected about a species at organism level.',NULL,'http://vocabulary.odm2.org/sampledmedium/organism');INSERT INTO "CV_SampledMedium" VALUES('rock','Rock','Specimen collected from a naturally occuring solid aggregate of one or more minerals.',NULL,'http://vocabulary.odm2.org/sampledmedium/rock');INSERT INTO "CV_SampledMedium" VALUES('other','Other','Other.',NULL,'http://vocabulary.odm2.org/sampledmedium/other');INSERT INTO "CV_SampledMedium" VALUES('sediment','Sediment','Specimen collected from material broken down by processes of weathering and erosion and subsequently transported by the action of wind, water, or ice, and/or by the force of gravity acting on the particles. Sensors may also be emplaced to measure sediment properties.',NULL,'http://vocabulary.odm2.org/sampledmedium/sediment');INSERT INTO "CV_SampledMedium" VALUES('particulate','Particulate','Specimen collected from particulates suspended in a paticulate-fluid mixture. Examples include particulates in water or air.',NULL,'http://vocabulary.odm2.org/sampledmedium/particulate');INSERT INTO "CV_SampledMedium" VALUES('tissue','Tissue','Sample of a living organism''s tissue or sensor emplaced to measure property of tissue.',NULL,'http://vocabulary.odm2.org/sampledmedium/tissue');INSERT INTO "CV_SampledMedium" VALUES('liquidOrganic','Liquid organic','Specimen collected as an organic liquid.',NULL,'http://vocabulary.odm2.org/sampledmedium/liquidOrganic');INSERT INTO "CV_SampledMedium" VALUES('liquidAqueous','Liquid aqueous','Specimen collected as liquid water or sensor emplaced to measure properties of water in sampled environment.',NULL,'http://vocabulary.odm2.org/sampledmedium/liquidAqueous');INSERT INTO "CV_SampledMedium" VALUES('mineral','Mineral','Specimen collected as a mineral.',NULL,'http://vocabulary.odm2.org/sampledmedium/mineral');INSERT INTO "CV_SampledMedium" VALUES('snow','Snow','Observation in, of or sample taken from snow.',NULL,'http://vocabulary.odm2.org/sampledmedium/snow');INSERT INTO "CV_SampledMedium" VALUES('unknown','Unknown','The sampled medium is unknown.',NULL,'http://vocabulary.odm2.org/sampledmedium/unknown');INSERT INTO "CV_SampledMedium" VALUES('gas','Gas','Gas phase specimen or sensor emplaced to measure properties of a gas.',NULL,'http://vocabulary.odm2.org/sampledmedium/gas');INSERT INTO "CV_SampledMedium" VALUES('soil','Soil','Specimen collected from soil or sensor emplaced to measure properties of soil. Soil includes the mixture of minerals, organic matter, gasses, liquids, and organisms that make up the upper layer of earth in which plants grow. ',NULL,'http://vocabulary.odm2.org/sampledmedium/soil');INSERT INTO "CV_SampledMedium" VALUES('notApplicable','Not applicable','There is no applicable sampled medium.',NULL,'http://vocabulary.odm2.org/sampledmedium/notApplicable');INSERT INTO "CV_SampledMedium" VALUES('ice','Ice','Sample collected as frozen water or sensor emplaced to measure properties of ice.',NULL,'http://vocabulary.odm2.org/sampledmedium/ice');CREATE TABLE CV_SamplingFeatureGeoType (
